import './Modal.css';

export default function Modal({ title, isVisible, toggleFn, children }) {
  const modalStyle = {
    display: (isVisible ? "block" : "none"),
  };

  return(
    <div className="modal-container" style={modalStyle}>
        <label forhtml="modal" className="modal-background"></label>
        <div className="modal">
          <div className="modal-header">
            <h3>{title}</h3>
                <label forhtml="modal" onClick={toggleFn}>
                  <img src="data:image/png;base64," width="16" height="16" alt="" />
                </label>
            </div>
            {children}
        </div>
      </div>
  );
}